.container-fluid {
    padding-right: 15px;
    padding-left: 15px;
    margin-right: auto;
    margin-left: auto;
    background: rgb(0,46,70)!important;
    background: linear-gradient(94deg, rgba(0,46,70,1) 0%, rgba(24,90,131,1) 100%);
}




/* Grid header */

body .form-cell .grid thead tr, body .subform-cell .grid thead tr {
    background: #c5c5c5;
    color: #000000;
    border-bottom: -15px solid #adadad;
}

/* customer deatils drop down feilds in form */

main select:not([class^="ui-"]):not([multiple]) {

    background-color: lightgrey;
}


@media (min-width: 768px) {
  body.horizontal_menu #sidebar {
    background: rgb(0, 46, 70)!important;
    background: linear-gradient(94deg, rgba(0, 46, 70, 1) 0%, rgba(24, 90, 131, 1) 100%);
    padding: 20px 23px 1px;
    }
}

row-count{
    color: #2338d5;
}

.btn.waves-input-wrapper {
    color: #ffffff !important;
    background-color: #2338d5 !important;
}


body #form-canvas .form-section-title span {
   background: rgb(0,46,70)!important;
    background: linear-gradient(94deg, rgba(0,46,70,1) 0%, rgba(24,90,131,1) 100%);
}

legend, body #form-canvas .form-section-title span {
    color: white;
    padding: 10px;
}

.btn.waves-input-wrapper {
    color: #ffffff !important;
    background-color: #2338d5 !important;
}

div#header-name img {
    max-height: 47px;
    display: inline;
    margin-bottom: 5px;
    margin-top: 2px;
    margin-right: 12px;
}

@media (min-width: 768px){
body.horizontal_menu header.navbar {
    box-shadow: 0 -1px -1px rgba(0,0,0,.14);
    position: relative;
    margin-bottom: 0;
}
}

/*APP LABEL */

#header-name a#header-link {
    display: inline-block;
    font-size: 24px;
    text-transform: uppercase;
    text-decoration: unset;
    vertical-align: initial;
    font-family: serif;
    font-weight: 500;
}



/*DATALIST HEADER COLOR */

body .dataList th, body .dataList th a:hover, body .dataList th a {
    color: #ffffff;
    border-bottom: 1px solid #adadad;
    font-weight: bolder;
    background: linear-gradient(179deg, rgba(0,66,101,1) 0%, rgba(26,147,210,1) 100%);
}

/*BUTTONS CSS*/

.btn, .btn.focus, .btn:focus, .btn:active {
    color: #ffffff;
    background-color: #1774a5;
    border-radius: 10px;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.2), 0 4px 4px -2px rgba(0, 0, 0, 0.2);
    margin: 1px 2px 0 0;
    border: 1px solid #1774a5;
    outline: 0;
}

/*Datalist edit button CSS*/

td.row_action.footable-visible.footable-last-column > span > a {
    color: #ffffff;
    background: #1774a5;
    padding: 4px 18px;
    border-radius: 10px;
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.2), 0 4px 4px -2px rgba(0, 0, 0, 0.2);
    font-size: 14px;
}

