/* 
Menu settings
*/
@media (min-width: 768px){
body.horizontal_menu #sidebar .main-menu.nav>li:hover >a, body.horizontal_menu #sidebar .main-menu.nav-tabs>li.active>a, body.horizontal_menu #sidebar .main-menu.nav-tabs>li:hover >a {
    background-color: #45618300;
    cursor: revert;
}
}

@media (min-width: 768px){
body.horizontal_menu #sidebar .main-menu.nav-tabs > li.active {
    background-color: #125891;
    border-radius: 9px!important;
     transition: background-color 0.3s, transform 0.3s;
}
body.horizontal_menu #sidebar .main-menu.nav-tabs > li.active {
     background-color:#125891;
    transform: scale(1.1);
  }
}

@media (min-width: 768px){
body.horizontal_menu:not(#login) div#content {
    padding: 0px 30px;
    background: #eee;
    background-image: url(/jw/web/app/payrollEarning/resources/v915-blue_53876-166891.jpg);
    background-repeat: repeat-y;
    background-size: cover;
}
